import React from 'react';

class AddProduct extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      idutilisateur: '',
      artice:'',
      password:'',
      image:''

    };
    this.onChange = this.onChange.bind(this)
    this.handleUploadImage = this.handleUploadImage.bind(this);
  }




  onChange(event) {
    this.setState({
        [event.target.name]: event.target.value
    })
}





  handleUploadImage(ev) {
    ev.preventDefault();

    const data = new FormData();
    data.append('image', this.uploadInput.files[0]);
    data.append('idutilisateur',this.state.idutilisateur);
    data.append('article',this.state.artice);

    fetch('http://localhost:8080/aricle', {
      method: 'POST',
      body: data,
    }).then((response) => {
      response.json().then((body) => {
        this.setState({ photo_profil: `http://localhost:8080/article/${body.photo_profil}` });
        console.log('ity ilay body.fil',body.photo_profil);

      });
    });
  }

  render() {
    return (
      <form onSubmit={this.handleUploadImage}>
        <label>idutilisateur:</label>
        <input type="text"
          value={this.state.value}
          onChange={this.onChange}
          name="idutilisateur" /><br></br>
          <label>article:</label>
        <input type="text"
          value={this.state.value}
          onChange={this.onChange}
          name="article" /><br></br>      

          <input ref={(ref) => { this.uploadInput = ref; }} type="file" name="image"/>

          <button>Ajouter</button>
      </form>
    );
  }
}

export default AddProduct;
