import React from 'react';

class AddProduct extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      idutilisateur: '',
      article:'',
      image:''

    };
    this.onChange = this.onChange.bind(this)
    this.handleUploadImage = this.handleUploadImage.bind(this);
  }




  onChange(event) {
    this.setState({
        [event.target.name]: event.target.value
    })
}





  handleUploadImage(ev) {
    ev.preventDefault();

    const data = new FormData();
    data.append('image', this.uploadInput.files[0]);
    data.append('idutilisateur',this.state.idutilisateur);
    data.append('article',this.state.article);

    fetch('http://localhost:8080/article', {
      method: 'POST',
      body: data,
    }).then((response) => {
      response.json().then((body) => {
        this.setState({ image: `http://localhost:8080/publication/${body.image}` });
        console.log('ity ilay body.fil',body.image);

      });
    });
    this.setState({
        idutilisateur:'',
        article:'',
        image:''
    })
  }

  render() {
    return (
      <form onSubmit={this.handleUploadImage}>
        <label>idutilisateur:</label>
        <input type="text"
          value={this.state.idutilisateur}
          onChange={this.onChange}
          name="idutilisateur" /><br></br>
          <label>article:</label>
        <input type="text"
          value={this.state.article}
          onChange={this.onChange}
          name="article" /><br></br>      

          <input ref={(ref) => { this.uploadInput = ref; }} type="file" name="image"/>

          <div class="d-flex justify-content-center mt-3 login_container">
        <input type="submit" value="Ajouter" className="btn btn-primary"/>
        </div>
      </form>
    );
  }
}

export default AddProduct;
